INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('010109ae9b974f6294b19e542379a32f','a817ee365045448fb67c75db9b586ec8',NULL,1,'Row','SELECT \r\n	T.\"GUID\" AS \"GUID\",\r\n    T.\"Date\" AS \"Date\",\r\n    T.\"Reference Number\" AS \"Number\",\r\n    CAST(OFF.\"Debit\" AS DECIMAL(64,2)) AS \"Amount\",\r\n    TY.\"Name\" AS \"Type\",\r\n    TY.\"Edit URL\" AS \"Edit URL\"\r\nFROM\r\n	( SELECT \"Transactions GUID\", SUM(Debit) AS Debit FROM \"Transaction Lines\" GROUP BY \"Transactions GUID\" HAVING SUM(Debit) <> 0 ) OFF\r\n    JOIN \"Transactions\" T ON OFF.\"Transactions GUID\" = T.\"GUID\"\r\n    JOIN \"Transaction Types\" TY ON T.\"Transaction Types GUID\" = TY.\"GUID\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('05f9bfc1d09542ccbee74aa9c3800e16','490b638915b74aecb816c888dea125e9',NULL,20,'Total','SELECT\r\n	SUM(\"Check Gross\") AS \"Check Gross\",\r\n	SUM(\"Check Amount\") AS \"Check Amount\"\r\nFROM\r\n	(\r\n		SELECT\r\n			CAST(PC.\"Adjusted Gross\" AS DECIMAL(64,2)) AS \"Check Gross\",\r\n			CAST(PC.\"Paycheck Amount\" AS DECIMAL(64,2)) AS \"Check Amount\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('0ad0468ea1e742968479de41e0906bbf','33abdf4f68f44d73b23fabe400280576',NULL,0,'Row','SELECT * FROM \r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n                WHEN \"Is Allowed\" <> 0 THEN \'Allowed\'\r\n			END AS \"Status\"\r\n		FROM \"Users\"\r\n	) \"TBL\"\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('0b2c90eb1f6f4fb09d50d8f2bd9a5eb1','f64ba5f5752a4473a5946fe68ddde21f',NULL,1,'Row','SELECT\r\n	\"GUID\",\r\n	\"Date\",\r\n    \"Contact\",\r\n    CASE \r\n        WHEN \"Their Number\" IS NOT NULL THEN CONCAT(\'Check \', \"Their Number\")\r\n        ELSE CONCAT(\'Receipt \', \"Our Number\")\r\n    END AS \"Number\",\r\n    \"Doc Type\",\r\n    \"Doc Number\",\r\n    CAST((\"Taxable\" * \"Percent\") AS DECIMAL(64,2)) AS \"Taxable\",\r\n    CAST((\"Nontaxable\" * \"Percent\") AS DECIMAL(64,2)) AS \"Nontaxable\",\r\n    CAST((\"Taxes\" * \"Percent\") AS DECIMAL(64,2)) AS \"Taxes\",\r\n    CAST(\"Pymt Total\" AS DECIMAL(64,2)) AS \"Pymt Total\",\r\n    \"Tax Rate\",\r\n    \"Tax Name\",\r\n    \"Edit URL\"\r\nFROM (\r\n	SELECT\r\n			P.\"GUID\",\r\n			P.\"Date\",\r\n			P.\"Contacts Display Name\" AS \"Contact\",\r\n			P.\"Their Number\",\r\n			P.\"Our Number\",\r\n            PA.\"Amount\" AS \"Pymt Total\",\r\n			CASE\r\n				WHEN D.\"Total\" <> 0 THEN PA.\"Amount\" / D.\"Total\" \r\n				ELSE 1\r\n			END AS \"Percent\",\r\n			D.\"Date\" AS \"Doc Date\",\r\n			DTT.\"Name\" AS \"Doc Type\",\r\n			D.\"Reference Number\" AS \"Doc Number\",\r\n			CASE\r\n				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n				ELSE -1\r\n			END * CAST( CASE\r\n				WHEN D.\"Tax Rate\" <> 0 THEN D.\"Taxable Subtotal\"\r\n				ELSE 0\r\n			END AS DECIMAL(64,2) ) AS \"Taxable\",\r\n			CASE\r\n				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n				ELSE -1\r\n			END * CAST( CASE\r\n				WHEN D.\"Tax Rate\" <> 0 THEN D.\"Nontaxable Subtotal\"\r\n				ELSE D.\"Nontaxable Subtotal\" + D.\"Taxable Subtotal\"\r\n			END AS DECIMAL(64,2) ) AS \"Nontaxable\",\r\n			CASE\r\n				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n				ELSE -1\r\n			END * CAST( D.\"Taxes\" AS DECIMAL(64,2) ) AS \"Taxes\",\r\n			CASE\r\n				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n				ELSE -1\r\n			END * CAST( D.\"Total\" AS DECIMAL(64,2) ) AS \"Total\",\r\n			CAST( (D.\"Tax Rate\" * 100) AS DECIMAL(64,2) ) AS \"Tax Rate\",\r\n			ST.\"Display Name\" AS \"Tax Name\",    \r\n			PTT.\"Edit URL\"\r\n		FROM \r\n			\"Payment Applications\" PA\r\n			JOIN \"Payments\" P ON PA.\"Payments GUID\" = P.\"GUID\"\r\n			JOIN \"Documents\" D ON PA.\"Documents GUID\" = D.\"GUID\"\r\n			JOIN \"Transaction Types\" DTT ON DTT.\"GUID\" = D.\"Document Types GUID\"\r\n			JOIN \"Transaction Types\" PTT ON PTT.\"GUID\" = P.\"Payment Types GUID\"\r\n			JOIN \"Sales Taxes\" ST ON ST.\"GUID\" = D.\"Sales Taxes GUID\"\r\n		WHERE\r\n			P.\"Payment Types GUID\" = \'2c12d6167d654604be3f533c38f1ad1e\'\r\n        	AND DATE(P.\"Date\") >= DATE({Starting})\r\n            AND DATE(P.\"Date\") <= DATE({Ending})\r\n            AND PA.\"Amount\" > 0\r\n            AND D.\"GUID\" NOT IN ( SELECT \"Prepayment Documents GUID\" FROM \"Payments\" )\r\n) TBL');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('105afe5ce5254783bbd32c811613ac88',NULL,'337d9d6e095f4f6c99ed447be7f4015f',3,'ExpenseHeading','SELECT\r\n	\"Field Name\" AS \"Name\"\r\nFROM\r\n	(\r\n		SELECT\r\n			REPLACE(PF.\"Name\", \' \', \'&nbsp;\') AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n    		AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Company Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('107731514d504aba8af5305be30741e7','88898672973041059e47961735e0c047',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\",\r\n			\"Abbreviation\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \"Unit Measures\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Abbreviation\" LIKE CONCAT(\'%\', IFNULL({Abbreviation}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"\r\n	');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('161a67c036bc4792ab9ff1beecfb03ab','ea6fdeb8d76247aa876dcab709b5de6e',NULL,20,'AssetTotal','SELECT\r\n    SUM(\r\n        CAST(\r\n            IFNULL(\r\n                ( \r\n                    SELECT SUM(\"Transaction Lines\".\"Debit\") \r\n                    FROM \r\n                        \"Transaction Lines\" \r\n                        JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                    WHERE \r\n                        \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                        AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n                )\r\n        , 0) AS DECIMAL(64,2))\r\n    ) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'cf190cadde734f98a348d5f6bd112db6\'');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('161c34c5f83046f5815750c5e5b6d246','30357e5cfb164c29a741e77f3de1cce7',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'6632ec00f5824aeca4a49bf21cbdaece\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        {Contact} IS NULL\r\n        OR \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', {Contact}, \'%\')\r\n    )\r\n    AND (\r\n        {Number} IS NULL\r\n        OR \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', {Number}, \'%\')\r\n    )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('170c3378007146cabad30296ddd6d941','290198ebc76e43cbb7be15de3e3d0ad7',NULL,0,'Row','SELECT * FROM \r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n                WHEN \"Is Allowed\" <> 0 THEN \'Allowed\'\r\n			END AS \"Status\"\r\n		FROM \"Groups\"\r\n	) \"TBL\"\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('1defd1d941184f1e8d2396d3c98125f7','cefd88dd219a4b6f8bc6dd30fd77f058',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'dedf79eddf7c4e348918d42e25b53309\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        {Contact} IS NULL\r\n        OR \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', {Contact}, \'%\')\r\n    )\r\n    AND (\r\n        {Number} IS NULL\r\n        OR \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', {Number}, \'%\')\r\n    )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('201a2d40490b4e149f87309dbd551f46','c30ad5f313f44d15b9d5a75dcc7200ad',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"Items\".\"GUID\",\r\n            \"Items\".\"Number\",\r\n            IFNULL(\"Items\".\"Sales Description\", \'\') AS \"Description\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \"Items\"\r\n	) TBL\r\nWHERE\r\n	\"Description\" LIKE CONCAT(\'%\', IFNULL({Description}, \'\'), \'%\')\r\n	AND \"Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('22589e8e0e874f2981abc9fd1a287637','bdcddb7c80a34302a83a23ac1ed5d309',NULL,0,'Row','SELECT * FROM \r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\"\r\n		FROM \"Securables\"\r\n	) \"TBL\"\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('286ad154cfd64d2d8e4145b619d705c6','fcb89d0333e4418f829803e34887edb7',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Payment Types\".\"GUID\",\r\n			\"Name\" AS \"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \r\n			\"Payment Types\"\r\n			JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Payment Types\".\"GUID\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('2fbcbc0929aa48c0aa5c8d446105af91','b48f778f1bbf4b87b7e37c97324d9b48',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Payments\".\"GUID\",\r\n			\"Transaction Types\".\"Name\" AS \"Type\",\r\n			\"Payments\".\"Date\",\r\n			\"Payments\".\"Our Number\",\r\n			\"Payments\".\"Contacts Display Name\" AS \"Contact\",\r\n			\"Payments\".\"Their Number\",\r\n			CONVERT(\"Payments\".\"Total\", DECIMAL(64,2)) AS \"Total\"\r\n		FROM \r\n			\"Payments\"\r\n			JOIN \"Transaction Types\" ON \"Payments\".\"Payment Types GUID\" = \"Transaction Types\".\"GUID\"\r\n        WHERE\r\n        	\"Payment Types GUID\" = \'a714a873202f4f12bb29a42ed8ed9b5c\'\r\n            AND DATE(\"Payments\".\"Date\") <= DATE({Ending})\r\n            AND DATE(\"Payments\".\"Date\") >= DATE({Starting})\r\n	) TBL\r\nWHERE\r\n    \"Contact\" LIKE CONCAT(\'%\', IFNULL({Contact}, \'\'), \'%\')\r\nORDER BY\r\n    \"Date\", \"Our Number\", \"Contact\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('2fde0c492eec42a4896c44097e6b5437',NULL,'c5916205329f4b5997312b10f6971534',3,'EmployeeTotalExpense','SELECT\r\n	CAST(IFNULL((\r\n        	SELECT\r\n        		CAST(SUM(PFV.\"Total\") AS DECIMAL(64,2))\r\n        	FROM \r\n        		\"Payroll Checks\" PC\r\n        		JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n        		JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n        		JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n    			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n        	WHERE\r\n        		PC.\"Is Template\" = 0\r\n        		AND PF.\"Name\" = \"Field Name\"\r\n        		AND PFT.\"Name\" = \"Field Type\"\r\n                AND PC.\"Employees GUID\" = {Employee GUID}\r\n    			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n            	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n            	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	), 0) AS DECIMAL(64, 2)) AS \"Value\"\r\nFROM\r\n	(\r\n		SELECT\r\n			PF.\"Name\" AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Company Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('337d9d6e095f4f6c99ed447be7f4015f','490b638915b74aecb816c888dea125e9',NULL,10,'Employee','SELECT\r\n	\"Employee GUID\",\r\n	\"Empoyee Name\"\r\nFROM\r\n	(\r\n		SELECT\r\n			C.\"GUID\" AS \"Employee GUID\",\r\n			C.\"Display Name\" AS \"Empoyee Name\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nGROUP BY\r\n	\"Employee GUID\",\r\n	\"Empoyee Name\"\r\nORDER BY\r\n	\"Empoyee Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('39c4438641ac49a6b6c03b73b4946495',NULL,'337d9d6e095f4f6c99ed447be7f4015f',2,'WithholdingHeading','SELECT\r\n	\"Field Name\" AS \"Name\"\r\nFROM\r\n	(\r\n		SELECT\r\n			REPLACE(PF.\"Name\", \' \', \'&nbsp;\') AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Employee Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('3ccae1e5c106446b9db5d0d2919f7617','6552470b48204c06b7a23271b472b6b7',NULL,0,'Row','SELECT * FROM\r\n    (\r\n    	SELECT\r\n    	    \"GUID\",\r\n    		\"Display Name\" AS \"Name\",\r\n            CONCAT(ROUND(\"Tax Rate\" * 100, 2), \'%\') AS \"Rate\"\r\n        FROM \"Sales Taxes\"\r\n        WHERE	\"Is Group\" = 1\r\n    ) TBL \r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Rate\" LIKE CONCAT(\'%\', IFNULL({Rate}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('40f4ddd6b6484b128c7b8246c2eae664','f6c1313616f24bcd827863490e2a661b',NULL,40,'ExpenseTotal','SELECT\r\n    SUM(\r\n        CAST(\r\n            IFNULL(\r\n                ( \r\n                    SELECT SUM(\"Transaction Lines\".\"Debit\") * -1\r\n                    FROM \r\n                        \"Transaction Lines\" \r\n                        JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                    WHERE \r\n                        \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                        AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n                )\r\n        , 0) AS DECIMAL(64,2))\r\n    ) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'ade6405dd31d40169b7ed222ecaa6b9e\'');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('438552bcb3e44b898c2d36b6e9324e65','ea6fdeb8d76247aa876dcab709b5de6e',NULL,50,'NetWorth','SELECT\r\n    SUM(\r\n        CAST(\r\n            IFNULL(\r\n                ( \r\n                    SELECT SUM(\"Transaction Lines\".\"Debit\") \r\n                    FROM \r\n                        \"Transaction Lines\" \r\n                        JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                    WHERE \r\n                        \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                        AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n                )\r\n        , 0) AS DECIMAL(64,2))\r\n    ) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" IN (\'cf190cadde734f98a348d5f6bd112db6\', \'ddc9a0f637b64af2adeb19d0f6399e19\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('45d23d6da8c741d8871b07069d58ae30',NULL,'05f9bfc1d09542ccbee74aa9c3800e16',3,'TotalExpense','SELECT\r\n	CAST(IFNULL((\r\n        	SELECT\r\n        		CAST(SUM(PFV.\"Total\") AS DECIMAL(64,2))\r\n        	FROM \r\n        		\"Payroll Checks\" PC\r\n        		JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n        		JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n        		JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n    			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n        	WHERE\r\n        		PC.\"Is Template\" = 0\r\n        		AND PF.\"Name\" = \"Field Name\"\r\n        		AND PFT.\"Name\" = \"Field Type\"\r\n    			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n            	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n            	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	), 0) AS DECIMAL(64, 2)) AS \"Value\",\r\n	\"Field Name\" AS \"Name\"\r\nFROM\r\n	(\r\n		SELECT\r\n			PF.\"Name\" AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Company Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('4689213448154b60ba8c4af3f0ad7465','2fc5847e6eba4c5784ce3258bd5b8f53',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'9d3821afd6fb47f9b2713d3cc574ceff\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        {Contact} IS NULL\r\n        OR \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', {Contact}, \'%\')\r\n    )\r\n    AND (\r\n        {Number} IS NULL\r\n        OR \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', {Number}, \'%\')\r\n    )\r\n    AND \"Documents\".\"GUID\" NOT IN ( SELECT \"Prepayment Documents GUID\" FROM \"Payments\" )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('4764261065be4f6398e5013ce8ed2b9b','08f4949be17a4d90a6a3a75b05b17fdf',NULL,1,'Row','SELECT * FROM (\r\n	SELECT\r\n		\"GUID\",\r\n		\"Contact\",\r\n		\"Date\",\r\n		\"Type\",\r\n		\"Number\",\r\n		CAST(\r\n			CASE\r\n				WHEN \"Type GUID\" = \'9d3821afd6fb47f9b2713d3cc574ceff\' THEN \"Total\" * 1\r\n				WHEN \"Type GUID\" = \'6632ec00f5824aeca4a49bf21cbdaece\' THEN \"Total\" * -1\r\n			END AS DECIMAL(64,2)\r\n		) AS \"Total\",\r\n		CAST(\r\n			CASE\r\n				WHEN \"Type GUID\" = \'9d3821afd6fb47f9b2713d3cc574ceff\' THEN \"Total\" * 1\r\n				WHEN \"Type GUID\" = \'6632ec00f5824aeca4a49bf21cbdaece\' THEN \"Total\" * -1\r\n			END - IFNULL(( \r\n				SELECT SUM(\"Amount\") \r\n				FROM \r\n					\"Payment Applications\" APP \r\n					JOIN \"Payments\" ON \"Payments\".\"GUID\" = APP.\"Payments GUID\"\r\n				WHERE \r\n					APP.\"Documents GUID\" = TBL.\"GUID\"\r\n					AND DATE(\"Payments\".\"Date\") <= DATE({As Of})\r\n			), 0) AS DECIMAL(64,2)\r\n		) AS \"Balance\",\r\n		\"Age\"\r\n	FROM (\r\n		SELECT\r\n			\"Documents\".\"GUID\",\r\n			\"Documents\".\"Date\",\r\n			\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n			\"Transaction Types\".\"Name\" AS \"Type\",\r\n			\"Document Types GUID\" AS \"Type GUID\",\r\n			\"Documents\".\"Reference Number\" AS \"Number\",\r\n			CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\",\r\n			\"Transaction Types\".\"Edit URL\",\r\n			DATEDIFF(DATE({As Of}), \"Documents\".\"Date\") AS \"Age\"\r\n		FROM\r\n			\"Documents\"\r\n			JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types GUID\"\r\n		WHERE\r\n			\"Document Types GUID\" IN (\'9d3821afd6fb47f9b2713d3cc574ceff\', \'6632ec00f5824aeca4a49bf21cbdaece\')\r\n			AND DATE(\"Documents\".\"Date\") <= DATE({As Of})\r\n	) TBL\r\n) TBL2\r\n	WHERE \"Balance\" <> 0');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('496038127611493a85537d090fffb10c','f6c1313616f24bcd827863490e2a661b',NULL,10,'Revenue','SELECT\r\n    \"Accounts\".\"GUID\",\r\n    \"Accounts\".\"Number\",\r\n    \"Accounts\".\"Name\",\r\n    \"Account Types\".\"Name\" AS \"Type\",\r\n    CAST(\r\n        IFNULL(\r\n            ( \r\n                SELECT SUM(\"Transaction Lines\".\"Debit\") * -1 \r\n                FROM \r\n                    \"Transaction Lines\" \r\n                    JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                WHERE \r\n                    \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                    AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n            )\r\n    , 0) AS DECIMAL(64,2)) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'2ddbad1cf8d04f0d9c44fa9771a15306\'\r\nORDER BY \"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('4f90103182ee4813a905a84c2187452f','01f2dff753214482b4a1ac02f6640914',NULL,1,'Row','SELECT\r\n    D.\"GUID\" AS \"GUID\",\r\n    D.\"Date\" AS \"Date\",\r\n	D.\"Contacts Display Name\" AS \"Contact\",\r\n	TT.\"Name\" AS \"Type\",\r\n    LEFT(D.\"Reference Number\", 24) AS \"Number\",\r\n	CASE\r\n		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        ELSE -1\r\n	END * CAST( CASE\r\n		WHEN D.\"Tax Rate\" <> 0 THEN D.\"Taxable Subtotal\"\r\n        ELSE 0\r\n	END AS DECIMAL(64,2) ) AS \"Taxable\",\r\n	CASE\r\n		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        ELSE -1\r\n	END * CAST( CASE\r\n		WHEN D.\"Tax Rate\" <> 0 THEN D.\"Nontaxable Subtotal\"\r\n        ELSE D.\"Nontaxable Subtotal\" + D.\"Taxable Subtotal\"\r\n	END AS DECIMAL(64,2) ) AS \"Nontaxable\",\r\n    CASE\r\n		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        ELSE -1\r\n	END * CAST( D.\"Taxes\" AS DECIMAL(64,2) ) AS \"Taxes\",\r\n    CASE\r\n		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        ELSE -1\r\n	END * CAST( D.\"Total\" AS DECIMAL(64,2) ) AS \"Total\",\r\n    CAST( (D.\"Tax Rate\" * 100) AS DECIMAL(64,2) ) AS \"Tax Rate\",\r\n    ST.\"Display Name\" AS \"Tax Name\",\r\n    TT.\"Edit URL\"\r\nFROM \r\n    \"Documents\" D\r\n    JOIN \"Transaction Types\" TT ON TT.\"GUID\" = D.\"Document Types GUID\"\r\n    JOIN \"Sales Taxes\" ST ON ST.\"GUID\" = D.\"Sales Taxes GUID\"\r\nWHERE\r\n	TT.\"GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n	AND DATE(D.\"Date\") >= DATE({Starting})\r\n    AND DATE(D.\"Date\") <= DATE({Ending})');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('5e82728029904c07a1f26808365af87a','ea6fdeb8d76247aa876dcab709b5de6e',NULL,30,'Liability','SELECT\r\n    \"Accounts\".\"GUID\",\r\n    \"Accounts\".\"Number\",\r\n    \"Accounts\".\"Name\",\r\n    \"Account Types\".\"Name\" AS \"Type\",\r\n    CAST(\r\n        IFNULL(\r\n            ( \r\n                SELECT SUM(\"Transaction Lines\".\"Debit\") \r\n                FROM \r\n                    \"Transaction Lines\" \r\n                    JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                WHERE \r\n                    \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                    AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n            )\r\n    , 0) AS DECIMAL(64,2)) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'ddc9a0f637b64af2adeb19d0f6399e19\'\r\nORDER BY \"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('61880deb23e64637842c816d972e5152','767a1911c68b4878a2ffc8dfd6974bc5',NULL,0,'Row','SELECT * FROM \r\n	(\r\n		SELECT \r\n			\"Logs\".\"GUID\",\r\n			\"Occured\",\r\n			\"Users\".\"Display Name\" AS \"User\",\r\n			\"Log\",\r\n            REPLACE(REPLACE(CASE\r\n				WHEN LOCATE(\'http\', \"Logs\".\"Details\") > 0 THEN LEFT(\"Logs\".\"Details\", LOCATE(\'http\', \"Logs\".\"Details\") - 3)\r\n				WHEN LENGTH(\"Logs\".\"Details\") < 255 THEN \"Logs\".\"Details\"\r\n                ELSE LEFT(\"Logs\".\"Details\", 255)\r\n			END, \'>\', \'&gt;\'), \'<\', \'&lt;\') AS \"Details\"\r\n		FROM \r\n			\"Logs\"\r\n			JOIN \"Users\" ON \"Users\".\"GUID\" = \"Users GUID\"\r\n		WHERE\r\n			DATE(\"Occured\") <= DATE({Ending})\r\n			AND DATE(\"Occured\") >= DATE({Starting})        \r\n	) \"TBL\"\r\nWHERE\r\n	\"Details\" LIKE CONCAT(\'%\', IFNULL({Details}, \'\'), \'%\')\r\n	AND \"Log\" LIKE CONCAT(\'%\', IFNULL({Log}, \'\'), \'%\')\r\n	AND \"User\" LIKE CONCAT(\'%\', IFNULL({User}, \'\'), \'%\')\r\nORDER BY\r\n	\"Occured\" DESC');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('6297a89330d74faeb4886f9cadb9b5c8','f6c1313616f24bcd827863490e2a661b',NULL,50,'PL','SELECT\r\n    SUM(\r\n        CAST(\r\n            IFNULL(\r\n                ( \r\n                    SELECT SUM(\"Transaction Lines\".\"Debit\") * -1\r\n                    FROM \r\n                        \"Transaction Lines\" \r\n                        JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                    WHERE \r\n                        \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                        AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n                )\r\n        , 0) AS DECIMAL(64,2))\r\n    ) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" IN (\'ade6405dd31d40169b7ed222ecaa6b9e\', \'2ddbad1cf8d04f0d9c44fa9771a15306\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('66030acdb15546a5b8ba6b0c9246535c','ed6c823f890a470fa23b637dae910117',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \r\n			\"Contact Types\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('6864a4f8ce694b22a297bac69ba25400','6eeab128b44446b6b67556f7418298ba',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Date\",\r\n			\"Number\",\r\n			\"Pay To The Order Of\" AS \"Employee\",\r\n			CONVERT(\"Paycheck Amount\", DECIMAL(64, 2)) AS \"Amount\",\r\n			CASE\r\n				WHEN \"Is Template\" = 1 THEN \'Template\'\r\n				ELSE \'Check\'\r\n			END AS \"Type\"\r\n		FROM \r\n			\"Payroll Checks\"\r\n        WHERE\r\n		    DATE(\"Date\") <= DATE({Ending})\r\n            AND DATE(\"Date\") >= DATE({Starting})\r\n	) TBL\r\nWHERE\r\n	\"Type\" LIKE CONCAT(\'%\', IFNULL({Type}, \'\'), \'%\')\r\n	AND IFNULL(\"Number\", \'\') LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n	AND \"Employee\" LIKE CONCAT(\'%\', IFNULL({Employee}, \'\'), \'%\')\r\n	AND \"Amount\" LIKE CONCAT(\'%\', IFNULL({Amount}, \'\'), \'%\')\r\nORDER BY\r\n    \"Type\" DESC,\r\n    \"Date\",\r\n    \"Number\",\r\n    \"Employee\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('69d3f986db384d9caaefe10657fbfcb7','92ecd1b71cf24a9b82538fcd9b1bf136',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Document Types\".\"GUID\",\r\n			\"Name\" AS \"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \r\n			\"Document Types\"\r\n			JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types\".\"GUID\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('6e232099609b4dfa8b0f45783e7bc195','ea6fdeb8d76247aa876dcab709b5de6e',NULL,10,'Asset','SELECT\r\n    \"Accounts\".\"GUID\",\r\n    \"Accounts\".\"Number\",\r\n    \"Accounts\".\"Name\",\r\n    \"Account Types\".\"Name\" AS \"Type\",\r\n    CAST(\r\n        IFNULL(\r\n            ( \r\n                SELECT SUM(\"Transaction Lines\".\"Debit\") \r\n                FROM \r\n                    \"Transaction Lines\" \r\n                    JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                WHERE \r\n                    \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                    AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n            )\r\n    , 0) AS DECIMAL(64,2)) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'cf190cadde734f98a348d5f6bd112db6\'\r\nORDER BY \"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('74df5d659bad473b94de835226cc1e8b','f64ba5f5752a4473a5946fe68ddde21f',NULL,2,'Total','SELECT \r\n    CAST( SUM(\"Taxable\") AS DECIMAL(64,2) ) AS \"Taxable\",\r\n    CAST( SUM(\"Nontaxable\") AS DECIMAL(64,2) ) AS \"Nontaxable\",\r\n    CAST( SUM(\"Taxes\") AS DECIMAL(64,2) ) AS \"Taxes\",\r\n    CAST( SUM(\"Pymt Total\") AS DECIMAL(64,2) ) AS \"Total\"\r\nFROM \r\n    (\r\n\r\n        SELECT\r\n        	\"GUID\",\r\n        	\"Date\",\r\n            \"Contact\",\r\n            CASE \r\n                WHEN \"Their Number\" IS NOT NULL THEN CONCAT(\'Check \', \"Their Number\")\r\n                ELSE CONCAT(\'Receipt \', \"Our Number\")\r\n            END AS \"Number\",\r\n            \"Doc Type\",\r\n            \"Doc Number\",\r\n            CAST((\"Taxable\" * \"Percent\") AS DECIMAL(64,2)) AS \"Taxable\",\r\n            CAST((\"Nontaxable\" * \"Percent\") AS DECIMAL(64,2)) AS \"Nontaxable\",\r\n            CAST((\"Taxes\" * \"Percent\") AS DECIMAL(64,2)) AS \"Taxes\",\r\n            CAST(\"Pymt Total\" AS DECIMAL(64,2)) AS \"Pymt Total\",\r\n            \"Tax Rate\",\r\n            \"Tax Name\",\r\n            \"Edit URL\"\r\n        FROM (\r\n        	SELECT\r\n        			P.\"GUID\",\r\n        			P.\"Date\",\r\n        			P.\"Contacts Display Name\" AS \"Contact\",\r\n        			P.\"Their Number\",\r\n        			P.\"Our Number\",\r\n                    PA.\"Amount\" AS \"Pymt Total\",\r\n        			CASE\r\n        				WHEN D.\"Total\" <> 0 THEN PA.\"Amount\" / D.\"Total\" \r\n        				ELSE 1\r\n        			END AS \"Percent\",\r\n        			D.\"Date\" AS \"Doc Date\",\r\n        			DTT.\"Name\" AS \"Doc Type\",\r\n        			D.\"Reference Number\" AS \"Doc Number\",\r\n        			CASE\r\n        				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        				ELSE -1\r\n        			END * CAST( CASE\r\n        				WHEN D.\"Tax Rate\" <> 0 THEN D.\"Taxable Subtotal\"\r\n        				ELSE 0\r\n        			END AS DECIMAL(64,2) ) AS \"Taxable\",\r\n        			CASE\r\n        				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        				ELSE -1\r\n        			END * CAST( CASE\r\n        				WHEN D.\"Tax Rate\" <> 0 THEN D.\"Nontaxable Subtotal\"\r\n        				ELSE D.\"Nontaxable Subtotal\" + D.\"Taxable Subtotal\"\r\n        			END AS DECIMAL(64,2) ) AS \"Nontaxable\",\r\n        			CASE\r\n        				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        				ELSE -1\r\n        			END * CAST( D.\"Taxes\" AS DECIMAL(64,2) ) AS \"Taxes\",\r\n        			CASE\r\n        				WHEN DTT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n        				ELSE -1\r\n        			END * CAST( D.\"Total\" AS DECIMAL(64,2) ) AS \"Total\",\r\n        			CAST( (D.\"Tax Rate\" * 100) AS DECIMAL(64,2) ) AS \"Tax Rate\",\r\n        			ST.\"Display Name\" AS \"Tax Name\",    \r\n        			PTT.\"Edit URL\"\r\n        		FROM \r\n        			\"Payment Applications\" PA\r\n        			JOIN \"Payments\" P ON PA.\"Payments GUID\" = P.\"GUID\"\r\n        			JOIN \"Documents\" D ON PA.\"Documents GUID\" = D.\"GUID\"\r\n        			JOIN \"Transaction Types\" DTT ON DTT.\"GUID\" = D.\"Document Types GUID\"\r\n        			JOIN \"Transaction Types\" PTT ON PTT.\"GUID\" = P.\"Payment Types GUID\"\r\n        			JOIN \"Sales Taxes\" ST ON ST.\"GUID\" = D.\"Sales Taxes GUID\"\r\n        		WHERE\r\n        			P.\"Payment Types GUID\" = \'2c12d6167d654604be3f533c38f1ad1e\'\r\n                	AND DATE(P.\"Date\") >= DATE({Starting})\r\n                    AND DATE(P.\"Date\") <= DATE({Ending})\r\n                    AND PA.\"Amount\" > 0\r\n                    AND D.\"GUID\" NOT IN ( SELECT \"Prepayment Documents GUID\" FROM \"Payments\" )\r\n        ) TBL\r\n\r\n    ) TBL2');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('781c05819fae400281f2c12d836a1d1b',NULL,'f0e0abf91c4646b8b8c84d9708dcc848',0,'Contacts','SELECT\r\n	\"Contacts GUID\",\r\n	\"Contact\"\r\nFROM \r\n    (\r\n    	SELECT\r\n    		\"Documents\".\"GUID\",\r\n    		\"Documents\".\"Date\",\r\n    		\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n    		\"Documents\".\"Contacts GUID\" AS \"Contacts GUID\",\r\n    		\"Transaction Types\".\"Name\" AS \"Type\",\r\n    		\"Document Types GUID\" AS \"Type GUID\",\r\n    		LEFT(\"Documents\".\"Reference Number\", 24) AS \"Number\",\r\n    		\"Transaction Types\".\"Edit URL\",\r\n    		DATEDIFF(DATE({As Of}), \"Documents\".\"Date\") AS \"Age\",\r\n    		CAST(\r\n    			CASE\r\n    				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n    				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n    			END AS DECIMAL(64,2)\r\n    		) AS \"Total\",\r\n    		CAST(\r\n    			CASE\r\n    				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n    				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n    			END - IFNULL(( \r\n    				SELECT SUM(\"Amount\") \r\n    				FROM \r\n    					\"Payment Applications\" APP \r\n    					JOIN \"Payments\" ON \"Payments\".\"GUID\" = APP.\"Payments GUID\"\r\n    				WHERE \r\n    					APP.\"Documents GUID\" = \"Documents\".\"GUID\"\r\n    					AND DATE(\"Payments\".\"Date\") <= DATE({As Of})\r\n    			), 0) AS DECIMAL(64,2)\r\n    		) AS \"Balance\"\r\n    	FROM\r\n    		\"Documents\"\r\n    		JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types GUID\"\r\n    		JOIN \"Contacts\" ON \"Contacts\".\"GUID\" = \"Documents\".\"Contacts GUID\"\r\n    	WHERE\r\n    		\"Document Types GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n    		AND DATE(\"Documents\".\"Date\") <= DATE({As Of})\r\n    		AND \"Contacts\".\"Contact Types GUID\" = {ContactTypeGUID}\r\n    ) TBL\r\nWHERE \"Balance\" <> 0\r\nGROUP BY\r\n    \"Contact\"\r\nORDER BY \r\n	\"Contact\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('7b71ef0991c546a69eee2ac990896117','cef027b31c704b17b9f97bc9489626cb',NULL,1,'Groups','SELECT * \r\nFROM \"Groups\" \r\nWHERE\r\n\"Groups\".\"Display Name\" LIKE CONCAT(\'%\', {Name}, \'%\') OR {Name} IS NULL');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('7ba54a189c9a47cdbcb4aaf434c55164','1b739cb0b80649589760652098d0ff9c',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Number\",\r\n			\"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \"Departments\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Number\",\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('7c89d923db3c4c42919675216c9ac18f','7f8c6d9d299049779046ad7eb70884fa',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'e56b2b6aa42b479890085b74b69275f3\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        {Contact} IS NULL\r\n        OR \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', {Contact}, \'%\')\r\n    )\r\n    AND (\r\n        {Number} IS NULL\r\n        OR \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', {Number}, \'%\')\r\n    )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('7ea9908ec62d456bab857ed989c13ad3','ee4f00312ff44e3d941c6a3445bf93b0',NULL,1,'Row','SELECT\r\n    CASE\r\n        WHEN \"Transaction Types\".\"GUID\" = \'38ec8da627f345eb9f0639c0931d5db8\' THEN \"Transaction Lines\".\"GUID\"\r\n        ELSE \"Transactions\".\"GUID\"\r\n    END AS \"GUID\",\r\n	\"Transactions\".\"Date\" AS \"Date\",\r\n    \"Transaction Types\".\"Name\" AS \"Type\",\r\n    \"Transactions\".\"Reference Number\" AS \"Number\",\r\n    \"Transaction Lines\".\"Description\" AS \"Description\",\r\n    CAST(\"Transaction Lines\".\"Debit\" AS DECIMAL(64,2)) AS \"Debit\",    \r\n    \"Transaction Types\".\"Edit URL\"\r\nFROM\r\n	\"Transactions\"\r\n    JOIN \"Transaction Lines\" ON \"Transaction Lines\".\"Transactions GUID\" = \"Transactions\".\"GUID\"\r\n    JOIN \"Transaction Types\" ON \"Transactions\".\"Transaction Types GUID\" = \"Transaction Types\".\"GUID\"\r\n    JOIN \"Accounts\" ON \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\"\r\nWHERE\r\n	DATE(\"Transactions\".\"Date\") <= DATE({Ending})\r\n	AND DATE(\"Transactions\".\"Date\") >= DATE({Starting})\r\n    AND \"Accounts GUID\" = IFNULL({Account}, \'\')\r\nORDER BY\r\n    \"Transactions\".\"Date\",\r\n    CAST(\"Transaction Lines\".\"Debit\" AS DECIMAL(64,2)),\r\n    \"Transactions\".\"Reference Number\",\r\n    \"Transaction Lines\".\"Sort Order\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('806b7499b0ca4676abb85e1bee2e6b08','84c704545c9140fb96ee5b9610fe632b',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Bank Deposits\".\"GUID\" As \"GUID\",\r\n			\"Accounts\".\"Name\" AS \"Account\",\r\n			CONVERT(\"Bank Deposits\".\"Date\", CHAR(1024)) AS \"Date\",\r\n			\"Bank Deposits\".\"Number\",\r\n			CONVERT(\"Bank Deposits\".\"Total\", DECIMAL(64,2)) AS \"Total\"\r\n		FROM \r\n			\"Bank Deposits\"\r\n			JOIN \"Accounts\" ON \"Bank Deposits\".\"Accounts GUID\" = \"Accounts\".\"GUID\"\r\n		WHERE\r\n		    DATE(\"Bank Deposits\".\"Date\") <= DATE({Ending})\r\n            AND DATE(\"Bank Deposits\".\"Date\") >= DATE({Starting})\r\n\r\n	) TBL\r\nWHERE\r\n	\"Account\" LIKE CONCAT(\'%\', IFNULL({Account}, \'\'), \'%\')\r\n	AND \"Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n	AND \"Total\" LIKE CONCAT(\'%\', IFNULL({Total}, \'\'), \'%\')\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('899eee600ec74dcc96e2e5a9ac7faf0a','f36ffc41ff32417caaded31c09ea34e2',NULL,0,'Row','SELECT * FROM \"Transaction Types\" ORDER BY \"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('8bb391baf302424d8944d48bce48373e','f145f297e9724ce3b83a558b327a3112',NULL,0,'Row','SELECT\r\n	T.\"Date\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'cf190cadde734f98a348d5f6bd112db6\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) AS DECIMAL(64,2))) AS \"Assets\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'ddc9a0f637b64af2adeb19d0f6399e19\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) * -1 AS DECIMAL(64,2))) AS \"Liabilities\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'40f7e36772f240999fca4a9d2c1b536f\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) * -1 AS DECIMAL(64,2))) AS \"Equities Stays Open\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'531c81d9f0c942aeb5b742160d6b3942\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) * -1 AS DECIMAL(64,2))) AS \"Equities Gets Closed\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'6ee39c8c894e45fbb7892175e3365a34\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) * -1 AS DECIMAL(64,2))) AS \"Retained Earnings\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'2ddbad1cf8d04f0d9c44fa9771a15306\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) * -1 AS DECIMAL(64,2))) AS \"Revenues\",\r\n	SUM(CAST(IFNULL(CASE\r\n		WHEN A.\"Account Types GUID\" = \'ade6405dd31d40169b7ed222ecaa6b9e\' THEN TL.\"Debit\"\r\n        ELSE 0\r\n	END, 0) AS DECIMAL(64,2))) AS \"Expenses\",\r\n	SUM(CAST(IFNULL(TL.\"Debit\", 0) AS DECIMAL(64,2))) AS \"Totals\"    \r\nFROM \r\n	\"Transaction Lines\" TL\r\n    JOIN \"Transactions\" T ON T.\"GUID\" = TL.\"Transactions GUID\"\r\n    JOIN \"Accounts\" A ON A.\"GUID\" = TL.\"Accounts GUID\"\r\nGROUP BY\r\n	T.\"Date\"\r\nORDER BY\r\n	T.\"Date\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('8e1134185167428caceccc3172a93aea',NULL,'781c05819fae400281f2c12d836a1d1b',0,'Documents','SELECT\r\n	\"GUID\",\r\n	\"Contact\",\r\n	\"Date\",\r\n	\"Type\",\r\n	\"Number\",\r\n	\"Total\",\r\n	\"Balance\",\r\n	\"Age\",\r\n	\"Edit URL\"\r\nFROM \r\n    (\r\n    	SELECT\r\n    		\"Documents\".\"GUID\",\r\n    		\"Documents\".\"Date\",\r\n    		\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n    		\"Transaction Types\".\"Name\" AS \"Type\",\r\n    		\"Document Types GUID\" AS \"Type GUID\",\r\n    		LEFT(\"Documents\".\"Reference Number\", 24) AS \"Number\",\r\n    		\"Transaction Types\".\"Edit URL\",\r\n    		DATEDIFF(DATE({As Of}), \"Documents\".\"Date\") AS \"Age\",\r\n    		CAST(\r\n    			CASE\r\n    				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n    				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n    			END AS DECIMAL(64,2)\r\n    		) AS \"Total\",\r\n    		CAST(\r\n    			CASE\r\n    				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n    				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n    			END - IFNULL(( \r\n    				SELECT SUM(\"Amount\") \r\n    				FROM \r\n    					\"Payment Applications\" APP \r\n    					JOIN \"Payments\" ON \"Payments\".\"GUID\" = APP.\"Payments GUID\"\r\n    				WHERE \r\n    					APP.\"Documents GUID\" = \"Documents\".\"GUID\"\r\n    					AND DATE(\"Payments\".\"Date\") <= DATE({As Of})\r\n    			), 0) AS DECIMAL(64,2)\r\n    		) AS \"Balance\"\r\n    	FROM\r\n    		\"Documents\"\r\n    		JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types GUID\"\r\n    	WHERE\r\n    		\"Document Types GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n    		AND DATE(\"Documents\".\"Date\") <= DATE({As Of})\r\n    		AND \"Documents\".\"Contacts GUID\" = {Contacts GUID}\r\n    ) TBL\r\nWHERE \"Balance\" <> 0\r\nORDER BY \r\n    \"Contact\",\r\n    \"Age\" DESC,\r\n    \"Balance\"\r\n    ');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('91cb388ac26041219bdc7216c26cb75a','6d4ea7d4624e48509e915e019ca1f7a9',NULL,0,'Account','SELECT\r\n  \"Accounts\".\"GUID\",\r\n  \"Accounts\".\"Number\",\r\n  \"Accounts\".\"Name\",\r\n  \"Account Types\".\"Name\" AS \"Account Types Name\",\r\n  CAST(\r\n    IFNULL(\r\n      ( \r\n        SELECT SUM(\"Transaction Lines\".\"Debit\") \r\n        FROM \r\n          \"Transaction Lines\" \r\n          JOIN \"Transactions\" \r\n            ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n        WHERE \r\n          \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n          AND DATE(\"Transactions\".\"Date\") <= DATE({As Of Date})\r\n      )\r\n    , 0) AS DECIMAL(64,2)) AS \"Balance\"\r\nFROM \r\n  \"Accounts\"\r\n  JOIN \"Account Types\" \r\n    ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nORDER BY \"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('9342c25154934e649b4347958c516f31','ee4f00312ff44e3d941c6a3445bf93b0',NULL,0,'BeginningBalance','SELECT\r\n    SUM(CAST(\"Transaction Lines\".\"Debit\" AS DECIMAL(64,2))) AS \"Debit\"\r\nFROM\r\n	\"Transactions\"\r\n    JOIN \"Transaction Lines\" ON \"Transaction Lines\".\"Transactions GUID\" = \"Transactions\".\"GUID\"\r\nWHERE\r\n	DATE(\"Transactions\".\"Date\") < DATE({Starting})\r\n    AND \"Accounts GUID\" = IFNULL({Account}, \'\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('98118ab59eab42e590e6ae79274a60b5',NULL,'e06a44b88abf4795ac92e8297f3c1ce1',3,'ExpenseColumn','SELECT\r\n	CAST(IFNULL((\r\n        	SELECT\r\n        		CAST(SUM(PFV.\"Total\") AS DECIMAL(64,2))\r\n        	FROM \r\n        		\"Payroll Checks\" PC\r\n        		JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n        		JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n        		JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n    			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n        	WHERE\r\n        		PC.\"Is Template\" = 0\r\n        		AND PC.\"GUID\" = {Check GUID}\r\n        		AND PF.\"Name\" = \"Field Name\"\r\n        		AND PFT.\"Name\" = \"Field Type\"\r\n    			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n            	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n            	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	), 0) AS DECIMAL(64, 2)) AS \"Value\"\r\nFROM\r\n	(\r\n		SELECT\r\n			PF.\"Name\" AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Company Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('99e65dccb16741738eed92c4046c2679','01f2dff753214482b4a1ac02f6640914',NULL,2,'Total','SELECT \r\n    CAST( SUM(TBL.\"Taxable\") AS DECIMAL(64,2) ) AS \"Taxable\",\r\n    CAST( SUM(TBL.\"Nontaxable\") AS DECIMAL(64,2) ) AS \"Nontaxable\",\r\n    CAST( SUM(TBL.\"Taxes\") AS DECIMAL(64,2) ) AS \"Taxes\",\r\n    CAST( SUM(TBL.\"Total\") AS DECIMAL(64,2) ) AS \"Total\"\r\nFROM \r\n    (\r\n        SELECT\r\n            D.\"Date\" AS \"Date\",\r\n        	D.\"Contacts Display Name\" AS \"Contact\",\r\n        	TT.\"Name\" AS \"Type\",\r\n            D.\"Reference Number\" AS \"Number\",\r\n        	CASE\r\n        		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n                ELSE -1\r\n        	END * CAST( CASE\r\n        		WHEN D.\"Tax Rate\" <> 0 THEN D.\"Taxable Subtotal\"\r\n                ELSE 0\r\n        	END AS DECIMAL(64,2) ) AS \"Taxable\",\r\n        	CASE\r\n        		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n                ELSE -1\r\n        	END * CAST( CASE\r\n        		WHEN D.\"Tax Rate\" <> 0 THEN D.\"Nontaxable Subtotal\"\r\n                ELSE D.\"Nontaxable Subtotal\" + D.\"Taxable Subtotal\"\r\n        	END AS DECIMAL(64,2) ) AS \"Nontaxable\",\r\n            CASE\r\n        		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n                ELSE -1\r\n        	END * CAST( D.\"Taxes\" AS DECIMAL(64,2) ) AS \"Taxes\",\r\n            CASE\r\n        		WHEN TT.\"GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN 1\r\n                ELSE -1\r\n        	END * CAST( D.\"Total\" AS DECIMAL(64,2) ) AS \"Total\",\r\n            CAST( (D.\"Tax Rate\" * 100) AS DECIMAL(64,2) ) AS \"Tax Rate\",\r\n            ST.\"Display Name\" AS \"Tax Name\"\r\n        FROM \r\n            \"Documents\" D\r\n            JOIN \"Transaction Types\" TT ON TT.\"GUID\" = D.\"Document Types GUID\"\r\n            JOIN \"Sales Taxes\" ST ON ST.\"GUID\" = D.\"Sales Taxes GUID\"\r\n        WHERE\r\n        	TT.\"GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n        	AND DATE(D.\"Date\") >= DATE({Starting})\r\n            AND DATE(D.\"Date\") <= DATE({Ending})\r\n    ) TBL');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('a3a44a67d9c943dcb8fc912cc28124c6',NULL,'7b71ef0991c546a69eee2ac990896117',1,'Users','SELECT * FROM \"Users\" \nWHERE \"Users\".\"GUID\" IN (\n\nSELECT \"Users GUID\" FROM \"Memberships\" \nWHERE \"Memberships\".\"Groups GUID\" = {GUID}\n\n)');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('a489818547ff4698a213c16845671591','a64f1e848b4b4b009f669e133d201478',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\",\r\n			\"Home Phone\" AS \"Home\",\r\n			\"Mobile Phone\" AS \"Mobile\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM\r\n			\"Contacts\"\r\n		WHERE\r\n			\"GUID\" IN ( SELECT  \"Employees\".\"GUID\" FROM \"Employees\" )\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND ( \"Home\" LIKE CONCAT(\'%\', IFNULL({Phone}, \'\'), \'%\') OR \"Mobile\" LIKE CONCAT(\'%\', IFNULL({Phone}, \'\'), \'%\') ) \r\n    AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('a8d5651c2f9d4e78a9ffcfd867cf8fbc','b8f0ac2f50ba4d40b227c841846dee15',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Payments\".\"GUID\",\r\n			\"Transaction Types\".\"Name\" AS \"Type\",\r\n			\"Payments\".\"Date\",\r\n			\"Payments\".\"Our Number\",\r\n			\"Payments\".\"Contacts Display Name\" AS \"Contact\",\r\n			\"Payments\".\"Their Number\",\r\n			CONVERT(\"Payments\".\"Total\", DECIMAL(64,2)) AS \"Total\"\r\n		FROM \r\n			\"Payments\"\r\n			JOIN \"Transaction Types\" ON \"Payments\".\"Payment Types GUID\" = \"Transaction Types\".\"GUID\"\r\n        WHERE\r\n        	\"Payment Types GUID\" = \'2c12d6167d654604be3f533c38f1ad1e\'\r\n            AND DATE(\"Payments\".\"Date\") <= DATE({Ending})\r\n            AND DATE(\"Payments\".\"Date\") >= DATE({Starting})\r\n	) TBL\r\nWHERE\r\n    \"Contact\" LIKE CONCAT(\'%\', IFNULL({Contact}, \'\'), \'%\')\r\nORDER BY\r\n    \"Date\", \"Our Number\", \"Contact\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('b48c56ef493c4f4eb0c6a3cadd33f691','57808ea5bb1948d8a1ddce32a3233e10',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Display Name\" AS \"Name\",\r\n			\"Office Phone\" AS \"Office\",\r\n			\"Mobile Phone\" AS \"Mobile\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM\r\n			\"Contacts\"\r\n		WHERE\r\n			\"Parent Contacts GUID\" IS NULL\r\n	) TBL\r\nWHERE\r\n    ({Name} IS NULL OR \"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	AND ( \r\n        ({Phone} IS NULL OR \"Office\" LIKE CONCAT(\'%\', IFNULL({Phone}, \'\'), \'%\'))\r\n        OR ({Phone} IS NULL OR \"Mobile\" LIKE CONCAT(\'%\', IFNULL({Phone}, \'\'), \'%\'))\r\n    ) \r\n    AND ({Status} IS NULL OR \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\'))\r\nORDER BY\r\n	\"Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('b58f5d49b7834af8ab021d6b31dfbbdc','ea6fdeb8d76247aa876dcab709b5de6e',NULL,40,'LiabilityTotal','SELECT\r\n    SUM(\r\n        CAST(\r\n            IFNULL(\r\n                ( \r\n                    SELECT SUM(\"Transaction Lines\".\"Debit\") \r\n                    FROM \r\n                        \"Transaction Lines\" \r\n                        JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                    WHERE \r\n                        \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                        AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n                )\r\n        , 0) AS DECIMAL(64,2))\r\n    ) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'ddc9a0f637b64af2adeb19d0f6399e19\'');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('b9858a22109f4269894d111c69dc79bd','d638b37131514266a0fa8df8dc0b39a6',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT \r\n			\"GUID\",\r\n			\"Number\",\r\n			\"Name\",\r\n			CASE\r\n				WHEN \"Is Allowed\" = 1 THEN \'Allowed\'\r\n				WHEN \"Is Allowed\" = 0 THEN \'Disabled\'\r\n			END AS \"Status\"\r\n		FROM \"Jobs\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n	AND \"Status\" LIKE CONCAT(\'%\', IFNULL({Status}, \'\'), \'%\')\r\nORDER BY\r\n	\"Number\",\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('bbca1a9d7d4e4afc9e92699ba71e5a59','f6c1313616f24bcd827863490e2a661b',NULL,30,'Expense','SELECT\r\n    \"Accounts\".\"GUID\",\r\n    \"Accounts\".\"Number\",\r\n    \"Accounts\".\"Name\",\r\n    \"Account Types\".\"Name\" AS \"Type\",\r\n    CAST(\r\n        IFNULL(\r\n            ( \r\n                SELECT SUM(\"Transaction Lines\".\"Debit\") * -1\r\n                FROM \r\n                    \"Transaction Lines\" \r\n                    JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                WHERE \r\n                    \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                    AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n            )\r\n    , 0) AS DECIMAL(64,2)) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'ade6405dd31d40169b7ed222ecaa6b9e\'\r\nORDER BY \"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('be3823b2e800477b8cbca5ef4377b299','b88b0031fc2844eb874c5be27f8be003',NULL,2,'Totals','SELECT\r\n    SUM(\"Balance\") AS \"Balance\",\r\n    CEIL(AVG(\"Age\")) AS \"Age\"\r\nFROM (\r\n    SELECT\r\n    	\"GUID\",\r\n    	\"Contact\",\r\n    	\"Date\",\r\n    	\"Type\",\r\n    	\"Number\",\r\n    	\"Total\",\r\n    	\"Balance\",\r\n    	\"Age\",\r\n    	\"Edit URL\"\r\n    FROM \r\n        (\r\n        	SELECT\r\n        		\"Documents\".\"GUID\",\r\n        		\"Documents\".\"Date\",\r\n        		\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n        		\"Transaction Types\".\"Name\" AS \"Type\",\r\n        		\"Document Types GUID\" AS \"Type GUID\",\r\n        		LEFT(\"Documents\".\"Reference Number\", 24) AS \"Number\",\r\n        		\"Transaction Types\".\"Edit URL\",\r\n        		DATEDIFF(DATE({As Of}), \"Documents\".\"Date\") AS \"Age\",\r\n        		CAST(\r\n        			CASE\r\n        				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n        				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n        			END AS DECIMAL(64,2)\r\n        		) AS \"Total\",\r\n        		CAST(\r\n        			CASE\r\n        				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n        				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n        			END - IFNULL(( \r\n        				SELECT SUM(\"Amount\") \r\n        				FROM \r\n        					\"Payment Applications\" APP \r\n        					JOIN \"Payments\" ON \"Payments\".\"GUID\" = APP.\"Payments GUID\"\r\n        				WHERE \r\n        					APP.\"Documents GUID\" = \"Documents\".\"GUID\"\r\n        					AND DATE(\"Payments\".\"Date\") <= DATE({As Of})\r\n        			), 0) AS DECIMAL(64,2)\r\n        		) AS \"Balance\"\r\n        	FROM\r\n        		\"Documents\"\r\n        		JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types GUID\"\r\n        	WHERE\r\n        		\"Document Types GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n        		AND DATE(\"Documents\".\"Date\") <= DATE({As Of})\r\n        ) TBL\r\n    WHERE \"Balance\" <> 0\r\n) TBL2');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('bfea1d2af2e1489d835581dcc8e9aec0','a7d53239fa82450db2e9d07594e7cce9',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Payroll Fields\".\"GUID\",\r\n			\"Payroll Fields\".\"Name\",\r\n			\"Payroll Field Types\".\"Name\" AS \"Type\"\r\n		FROM\r\n			\"Payroll Fields\"\r\n			JOIN \"Payroll Field Types\" \r\n				ON \"Payroll Fields\".\"Payroll Field Types GUID\" = \"Payroll Field Types\".\"GUID\"\r\n	) TBL\r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Type\" LIKE CONCAT(\'%\', IFNULL({Type}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('c23b928f69c64a4fa349c4393170dc68','352f588dfcd147fda29b8b4cdc2fcdfc',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        LENGTH(IFNULL({Contact}, \'\')) = 0 OR\r\n        \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', IFNULL({Contact}, \'\'), \'%\')\r\n    )\r\n	AND (\r\n        LENGTH(IFNULL({Number}, \'\')) = 0 OR\r\n        \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n    )\r\n    AND \"Documents\".\"GUID\" NOT IN ( SELECT \"Prepayment Documents GUID\" FROM \"Payments\" )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('c31b2872113748179f14a4d9c6ca8ec8','ee4f00312ff44e3d941c6a3445bf93b0',NULL,2,'EndingBalance','SELECT\r\n    SUM(CAST(\"Transaction Lines\".\"Debit\" AS DECIMAL(64,2))) AS \"Debit\"\r\nFROM\r\n	\"Transactions\"\r\n    JOIN \"Transaction Lines\" ON \"Transaction Lines\".\"Transactions GUID\" = \"Transactions\".\"GUID\"\r\nWHERE\r\n	DATE(\"Transactions\".\"Date\") <= DATE({Ending})\r\n    AND \"Accounts GUID\" = IFNULL({Account}, \'\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('c5629584f1314e30b2f33ddfd1a672ec','7788774678b84145b3681511b0cc9d40',NULL,0,'Row','SELECT *\r\nFROM\r\n	(\r\n		SELECT\r\n			\"Reconciliations\".\"GUID\",\r\n			\"Date\",\r\n			CONCAT(\"Accounts\".\"Number\", \' \', \"Accounts\".\"Name\") AS \"Account\",\r\n			CONVERT(\"Statement Ending Balance\", DECIMAL(64,2)) AS \"Balance\"\r\n		FROM \"Reconciliations\"\r\n			JOIN \"Accounts\" ON \"Accounts\".\"GUID\" = \"Reconciliations\".\"Accounts GUID\"\r\n		WHERE\r\n		    DATE(\"Date\") <= DATE({Ending})\r\n            AND DATE(\"Date\") >= DATE({Starting})\r\n	) TBL\r\nWHERE\r\n	\"Account\" LIKE CONCAT(\'%\', IFNULL({Account}, \'\'), \'%\')\r\n	AND \"Balance\" LIKE CONCAT(\'%\', IFNULL({Balance}, \'\'), \'%\')\r\nORDER BY\r\n    \"Account\",\r\n    \"Date\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('c5916205329f4b5997312b10f6971534',NULL,'337d9d6e095f4f6c99ed447be7f4015f',5,'EmployeeTotal','SELECT\r\n	SUM(\"Check Gross\") AS \"Check Gross\",\r\n	SUM(\"Check Amount\") AS \"Check Amount\"\r\nFROM\r\n	(\r\n		SELECT\r\n			CAST(PC.\"Adjusted Gross\" AS DECIMAL(64,2)) AS \"Check Gross\",\r\n			CAST(PC.\"Paycheck Amount\" AS DECIMAL(64,2)) AS \"Check Amount\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n            AND PC.\"Employees GUID\" = {Employee GUID}\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('c6106908073e42faadf6acd3472a3d0f','75ecee50436c4a7d8095b30f794f87f9',NULL,0,'Row','SELECT \r\n	\"GUID\",\r\n    \"Display Name\" AS \"Name\"\r\nFROM \"Reports\"\r\nWHERE\r\n    \"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\nORDER BY \"Display Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('ccd8f3223f53435db75c145b54b98fba','f6c1313616f24bcd827863490e2a661b',NULL,20,'RevenueTotal','SELECT\r\n    SUM(\r\n        CAST(\r\n            IFNULL(\r\n                ( \r\n                    SELECT SUM(\"Transaction Lines\".\"Debit\") * -1\r\n                    FROM \r\n                        \"Transaction Lines\" \r\n                        JOIN \"Transactions\" ON \"Transactions\".\"GUID\" = \"Transaction Lines\".\"Transactions GUID\"\r\n                    WHERE \r\n                        \"Transaction Lines\".\"Accounts GUID\" = \"Accounts\".\"GUID\" \r\n                        AND DATE(\"Transactions\".\"Date\") <= DATE({As Of})\r\n                )\r\n        , 0) AS DECIMAL(64,2))\r\n    ) AS \"Debits\"\r\nFROM \r\n    \"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Account Types\".\"GUID\" = \'2ddbad1cf8d04f0d9c44fa9771a15306\'');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('cd0de178737f48c2a3ee182be87c7384',NULL,'e06a44b88abf4795ac92e8297f3c1ce1',2,'WithholdingColumn','SELECT\r\n	CAST(IFNULL((\r\n        	SELECT\r\n        		CAST(SUM(PFV.\"Total\") AS DECIMAL(64,2))\r\n        	FROM \r\n        		\"Payroll Checks\" PC\r\n        		JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n        		JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n        		JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n    			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n        	WHERE\r\n        		PC.\"Is Template\" = 0\r\n        		AND PC.\"GUID\" = {Check GUID}\r\n        		AND PF.\"Name\" = \"Field Name\"\r\n        		AND PFT.\"Name\" = \"Field Type\"\r\n    			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n            	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n            	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	), 0) AS DECIMAL(64, 2)) AS \"Value\"\r\nFROM\r\n	(\r\n		SELECT\r\n			PF.\"Name\" AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Employee Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('ced47ce82eef400e9b2d5e94d8213fec',NULL,'05f9bfc1d09542ccbee74aa9c3800e16',2,'TotalWithholding','SELECT\r\n	CAST(IFNULL((\r\n        	SELECT\r\n        		CAST(SUM(PFV.\"Total\") AS DECIMAL(64,2))\r\n        	FROM \r\n        		\"Payroll Checks\" PC\r\n        		JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n        		JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n        		JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n    			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n        	WHERE\r\n        		PC.\"Is Template\" = 0\r\n        		AND PF.\"Name\" = \"Field Name\"\r\n        		AND PFT.\"Name\" = \"Field Type\"\r\n    			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n            	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n            	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	), 0) AS DECIMAL(64, 2)) AS \"Value\",\r\n	\"Field Name\" AS \"Name\"\r\nFROM\r\n	(\r\n		SELECT\r\n			PF.\"Name\" AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Employee Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('d469da5dc0d94410bd69de0ef0b02378','766ef959263043c79244f932e49ff61d',NULL,0,'Row','SELECT * FROM\r\n    (\r\n    	SELECT\r\n    	    \"GUID\",\r\n    		\"Display Name\" AS \"Name\",\r\n            CONCAT(ROUND(\"Tax Rate\" * 100, 2), \'%\') AS \"Rate\"\r\n        FROM \"Sales Taxes\"\r\n        WHERE	\"Is Group\" = 0\r\n    ) TBL \r\nWHERE\r\n	\"Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n	AND \"Rate\" LIKE CONCAT(\'%\', IFNULL({Rate}, \'\'), \'%\')\r\nORDER BY\r\n	\"Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('e06a44b88abf4795ac92e8297f3c1ce1',NULL,'337d9d6e095f4f6c99ed447be7f4015f',4,'Check','SELECT\r\n	\"Check GUID\",\r\n	\"Check Date\",\r\n	\"Check Number\",\r\n	\"Check Gross\",\r\n	\"Check Amount\",\r\n	\"Check Expense\"    \r\nFROM\r\n	(\r\n		SELECT\r\n			PC.\"GUID\" AS \"Check GUID\",\r\n			PC.\"Number\" AS \"Check Number\",\r\n			PC.\"Date\" AS \"Check Date\",\r\n			CAST(PC.\"Adjusted Gross\" AS DECIMAL(64,2)) AS \"Check Gross\",\r\n			CAST(PC.\"Paycheck Amount\" AS DECIMAL(64,2)) AS \"Check Amount\",\r\n			CAST(PC.\"Total Costs\" AS DECIMAL(64,2)) AS \"Check Expense\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n            AND PC.\"Employees GUID\" = {Employee GUID}\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nGROUP BY\r\n	\"Check GUID\",\r\n	\"Check Number\",\r\n	\"Check Date\",\r\n	\"Check Gross\",\r\n	\"Check Amount\",\r\n	\"Check Expense\"\r\nORDER BY\r\n	\"Check Date\",\r\n	\"Check Number\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('e20613bfd60b491dae01735814c7f4a2',NULL,'c5916205329f4b5997312b10f6971534',2,'EmployeeTotalWithholding','SELECT\r\n	CAST(IFNULL((\r\n        	SELECT\r\n        		CAST(SUM(PFV.\"Total\") AS DECIMAL(64,2))\r\n        	FROM \r\n        		\"Payroll Checks\" PC\r\n        		JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n        		JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n        		JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n    			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n        	WHERE\r\n        		PC.\"Is Template\" = 0\r\n        		AND PF.\"Name\" = \"Field Name\"\r\n        		AND PFT.\"Name\" = \"Field Type\"\r\n                AND PC.\"Employees GUID\" = {Employee GUID}\r\n    			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n            	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n            	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	), 0) AS DECIMAL(64, 2)) AS \"Value\"\r\nFROM\r\n	(\r\n		SELECT\r\n			PF.\"Name\" AS \"Field Name\",\r\n			PFT.\"Name\" AS \"Field Type\"\r\n		FROM \r\n			\"Payroll Checks\" PC\r\n			JOIN \"Payroll Field Values\" PFV ON PFV.\"Payroll Checks GUID\" = PC.\"GUID\"\r\n			JOIN \"Payroll Fields\" PF ON PFV.\"Payroll Fields GUID\" = PF.\"GUID\"\r\n			JOIN \"Payroll Field Types\" PFT ON PF.\"Payroll Field Types GUID\" = PFT.\"GUID\"\r\n			JOIN \"Contacts\" C ON C.\"GUID\" = PC.\"Employees GUID\"\r\n		WHERE\r\n			PC.\"Is Template\" = 0\r\n			AND DATE(PC.\"Date\") <= DATE({Ending})\r\n        	AND DATE(PC.\"Date\") >= DATE({Starting})\r\n        	AND ({Name} IS NULL OR C.\"Display Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\'))\r\n	) TBL\r\nWHERE\r\n	\"Field Type\" = \'Employee Paid\'\r\nGROUP BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\nORDER BY\r\n	\"Field Type\",\r\n	\"Field Name\"\r\n');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('e20c0fe3559944f58fd3c2f4d831fa97','a92a85f5192e4dbb80cba3a67ad31448',NULL,0,'Row','SELECT * FROM \r\n    (\r\n    	SELECT\r\n    		\"Transactions\".\"GUID\",\r\n    		\"Date\",\r\n    		\"Transactions\".\"Reference Number\" AS \"Number\",\r\n    		\"Transaction Types\".\"Name\" AS \"Type\",\r\n    		CAST(\"Debits\".\"Debit\" AS DECIMAL(64,2)) AS \"Amount\"\r\n    	FROM\r\n    		\"Transactions\"\r\n    		JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Transactions\".\"Transaction Types GUID\"\r\n    		JOIN ( \r\n    			SELECT \r\n    				\"Transactions GUID\", \r\n    				SUM(\"Transaction Lines\".\"Debit\") AS \"Debit\" \r\n    			FROM \r\n    				\"Transaction Lines\" \r\n    			WHERE\r\n    				\"Transaction Lines\".\"Debit\" > 0\r\n    			GROUP BY \"Transactions GUID\"\r\n    		) \"Debits\" ON \"Transactions\".\"GUID\" = \"Transactions GUID\"\r\n    	WHERE\r\n    	    DATE(\"Date\") <= DATE({Ending})\r\n            AND DATE(\"Date\") >= DATE({Starting})\r\n    ) TBL	\r\nWHERE\r\n	\"Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n	AND \"Type\" LIKE CONCAT(\'%\', IFNULL({Type}, \'\'), \'%\')\r\n	AND \"Amount\" LIKE CONCAT(\'%\', IFNULL({Amount}, \'\'), \'%\')\r\nORDER BY\r\n    \"Date\" DESC,\r\n    \"Number\" DESC,\r\n    \"Amount\" DESC\r\n    ');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('e4c0cfd6b36643ac8374d7ae018b36d0','c545d2f279be49e9a471a43033d01c7f',NULL,0,'Row','SELECT * FROM \r\n	(\r\n		SELECT \r\n			\"Settings\".\"GUID\",\r\n            \"Key\" AS \"Key\",\r\n			IFNULL(\"Display Name\", \'\') AS \"User\"\r\n		FROM \r\n			\"Settings\"\r\n			LEFT JOIN \"Users\" ON \"Users GUID\" = \"Users\".\"GUID\"\r\n	) \"TBL\"\r\nWHERE\r\n	\"Key\" LIKE CONCAT(\'%\', IFNULL({Key}, \'\'), \'%\')\r\n	AND \"User\" LIKE CONCAT(\'%\', IFNULL({User}, \'\'), \'%\')');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('e54c8ab136754eaa960dfc17538671f4','06db3e1b55e54bc2babf0424e0843d5b',NULL,0,'Row','SELECT\r\n    \"Accounts\".\"GUID\" AS \"GUID\",\r\n    \"Accounts\".\"Number\" AS \"Number\",\r\n    \"Accounts\".\"Nested Name\" AS \"Name\",\r\n    \"Account Types\".\"Name\" AS \"Type\",\r\n    CASE\r\n        WHEN \"Accounts\".\"Is Allowed\" = 1 THEN \'True\'\r\n        ELSE \'False\'\r\n    END AS \"Allowed\"\r\nFROM\r\n	\"Accounts\"\r\n    JOIN \"Account Types\" ON \"Accounts\".\"Account Types GUID\" = \"Account Types\".\"GUID\"\r\nWHERE\r\n    \"Accounts\".\"Number\" LIKE CONCAT(\'%\', IFNULL({Number}, \'\'), \'%\')\r\n    AND \"Accounts\".\"Nested Name\" LIKE CONCAT(\'%\', IFNULL({Name}, \'\'), \'%\')\r\n    AND \"Account Types\".\"Name\" LIKE CONCAT(\'%\', IFNULL({Type}, \'\'), \'%\')\r\nORDER BY\r\n    \"Accounts\".\"Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('f0e0abf91c4646b8b8c84d9708dcc848','b88b0031fc2844eb874c5be27f8be003',NULL,1,'ContactTypes','SELECT\r\n	\"GUID\" AS \"ContactTypeGUID\",\r\n	\"Display Name\" AS \"ContactTypeName\"\r\nFROM \r\n	\"Contact Types\"\r\nWHERE\r\n    \"GUID\" IN (\r\n        SELECT\r\n        	\"Contact Types GUID\"\r\n        FROM \r\n            (\r\n            	SELECT\r\n            		\"Documents\".\"GUID\",\r\n            		\"Documents\".\"Date\",\r\n            		\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n            		\"Documents\".\"Contacts GUID\" AS \"Contacts GUID\",\r\n            		\"Contacts\".\"Contact Types GUID\" AS \"Contact Types GUID\",\r\n            		\"Transaction Types\".\"Name\" AS \"Type\",\r\n            		\"Document Types GUID\" AS \"Type GUID\",\r\n            		LEFT(\"Documents\".\"Reference Number\", 24) AS \"Number\",\r\n            		\"Transaction Types\".\"Edit URL\",\r\n            		DATEDIFF(DATE({As Of}), \"Documents\".\"Date\") AS \"Age\",\r\n            		CAST(\r\n            			CASE\r\n            				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n            				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n            			END AS DECIMAL(64,2)\r\n            		) AS \"Total\",\r\n            		CAST(\r\n            			CASE\r\n            				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n            				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n            			END - IFNULL(( \r\n            				SELECT SUM(\"Amount\") \r\n            				FROM \r\n            					\"Payment Applications\" APP \r\n            					JOIN \"Payments\" ON \"Payments\".\"GUID\" = APP.\"Payments GUID\"\r\n            				WHERE \r\n            					APP.\"Documents GUID\" = \"Documents\".\"GUID\"\r\n            					AND DATE(\"Payments\".\"Date\") <= DATE({As Of})\r\n            			), 0) AS DECIMAL(64,2)\r\n            		) AS \"Balance\"\r\n            	FROM\r\n            		\"Documents\"\r\n            		JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types GUID\"\r\n            		JOIN \"Contacts\" ON \"Contacts\".\"GUID\" = \"Documents\".\"Contacts GUID\"\r\n            	WHERE\r\n            		\"Document Types GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n            		AND DATE(\"Documents\".\"Date\") <= DATE({As Of})\r\n            ) TBL\r\n        WHERE \"Balance\" <> 0\r\n        GROUP BY\r\n            \"Contact Types GUID\"\r\n    )\r\nORDER BY\r\n	\"Display Name\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('f3c904df452e4a4a8d1ff5611a2a6d43',NULL,'781c05819fae400281f2c12d836a1d1b',1,'DocumentTotals','SELECT\r\n    SUM(\"Balance\") AS \"Balance\",\r\n    CEIL(AVG(\"Age\")) AS \"Age\"\r\nFROM (\r\n    SELECT\r\n    	\"GUID\",\r\n    	\"Contact\",\r\n    	\"Date\",\r\n    	\"Type\",\r\n    	\"Number\",\r\n    	\"Total\",\r\n    	\"Balance\",\r\n    	\"Age\",\r\n    	\"Edit URL\"\r\n    FROM \r\n        (\r\n        	SELECT\r\n        		\"Documents\".\"GUID\",\r\n        		\"Documents\".\"Date\",\r\n        		\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n        		\"Transaction Types\".\"Name\" AS \"Type\",\r\n        		\"Document Types GUID\" AS \"Type GUID\",\r\n        		LEFT(\"Documents\".\"Reference Number\", 24) AS \"Number\",\r\n        		\"Transaction Types\".\"Edit URL\",\r\n        		DATEDIFF(DATE({As Of}), \"Documents\".\"Date\") AS \"Age\",\r\n        		CAST(\r\n        			CASE\r\n        				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n        				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n        			END AS DECIMAL(64,2)\r\n        		) AS \"Total\",\r\n        		CAST(\r\n        			CASE\r\n        				WHEN \"Document Types GUID\" = \'81e2917ac5c34d1cb6f9d168cd0439db\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * 1\r\n        				WHEN \"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\' THEN CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) * -1\r\n        			END - IFNULL(( \r\n        				SELECT SUM(\"Amount\") \r\n        				FROM \r\n        					\"Payment Applications\" APP \r\n        					JOIN \"Payments\" ON \"Payments\".\"GUID\" = APP.\"Payments GUID\"\r\n        				WHERE \r\n        					APP.\"Documents GUID\" = \"Documents\".\"GUID\"\r\n        					AND DATE(\"Payments\".\"Date\") <= DATE({As Of})\r\n        			), 0) AS DECIMAL(64,2)\r\n        		) AS \"Balance\"\r\n        	FROM\r\n        		\"Documents\"\r\n        		JOIN \"Transaction Types\" ON \"Transaction Types\".\"GUID\" = \"Document Types GUID\"\r\n        	WHERE\r\n        		\"Document Types GUID\" IN (\'86af180c412f40c5a660678e3895694b\', \'81e2917ac5c34d1cb6f9d168cd0439db\')\r\n        		AND DATE(\"Documents\".\"Date\") <= DATE({As Of})\r\n        		AND \"Documents\".\"Contacts GUID\" = {Contacts GUID}\r\n        ) TBL\r\n    WHERE \"Balance\" <> 0\r\n    ORDER BY \r\n        \"Contact\",\r\n        \"Age\" DESC,\r\n        \"Balance\"\r\n) TBL2');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('f6be2c103b2e4acbb0e4329c66489967','3293e7a3b08744c38975accc635d7573',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'86af180c412f40c5a660678e3895694b\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        {Contact} IS NULL\r\n        OR \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', {Contact}, \'%\')\r\n    )\r\n    AND (\r\n        {Number} IS NULL\r\n        OR \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', {Number}, \'%\')\r\n    )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
INSERT INTO "Report Blocks" ("GUID", "Reports GUID", "Parent Block GUID", "Priority", "Name", "SQL Query") VALUES ('f8ecb7bab1cb4415b7ab56b185bc61e0','078c5cd40a024563bfde3641352d4e43',NULL,0,'Row','SELECT\r\n	\"Documents\".\"GUID\",\r\n	\"Documents\".\"Date\",\r\n	\"Documents\".\"Reference Number\" AS \"Number\",\r\n	\"Documents\".\"Contacts Display Name\" AS \"Contact\",\r\n	CAST(\"Documents\".\"Total\" AS DECIMAL(64,2)) AS \"Total\"\r\nFROM\r\n	\"Documents\"\r\nWHERE\r\n	\"Documents\".\"Document Types GUID\" = \'276db4afcf634b6fbc4a5821c9858ab9\'\r\n    AND DATE(\"Documents\".\"Date\") <= DATE({Ending})\r\n    AND DATE(\"Documents\".\"Date\") >= DATE({Starting})\r\n    AND (\r\n        {Contact} IS NULL\r\n        OR \"Documents\".\"Contacts Display Name\" LIKE CONCAT(\'%\', {Contact}, \'%\')\r\n    )\r\n    AND (\r\n        {Number} IS NULL\r\n        OR \"Documents\".\"Reference Number\" LIKE CONCAT(\'%\', {Number}, \'%\')\r\n    )\r\nORDER BY\r\n    \"Documents\".\"Date\" DESC,\r\n    \"Documents\".\"Reference Number\"');
