INSERT INTO "Reports" ("GUID", "Display Name", "HTML Template", "Title", "Auto Load") VALUES ('f145f297e9724ce3b83a558b327a3112','Graph - Accountng - Revenue vs Expenses','<style>\r\n    .axis path, .axis line {\r\n      fill: none;\r\n      stroke-width: 2px;\r\n      stroke: #000;\r\n    }\r\n\r\n    .data {\r\n      fill: none;\r\n      stroke-width: 1px;\r\n    }\r\n\r\n    .revenues {\r\n      stroke: #080;\r\n    }\r\n    .expenses {\r\n      stroke: #f00;\r\n    }\r\n</style>\r\n\r\n<svg id=\"chart\" viewBox=\"0 0 500 250\"></svg>\r\n<script src=\"~/scripts/d3.v3.min.js\"></script>\r\n<script>\r\nvar arrData = [];\r\n<!-- BEGIN Row -->\r\narrData.push({\r\n    date: d3.time.format(\"%Y-%m-%d\").parse(\"{Date}\"),\r\n    assets: {Assets},\r\n    liabilities: {Liabilities},\r\n    egc: {Equities Gets Closed},\r\n    eso: {Equities Stays Open},\r\n    re: {Retained Earnings},\r\n    revenues: {Revenues},\r\n    expenses: {Expenses}\r\n});\r\n<!-- END Row -->\r\n\r\nvar margin = {top: 0, right: 20, bottom: 20, left: 40};\r\nvar iWidth = 500;\r\nvar iHeight = iWidth/2;\r\nvar x = d3.time.scale().range([0, iWidth - margin.left - margin.right]);\r\nvar y = d3.scale.linear().range([iHeight - margin.top - margin.bottom, 0]);\r\nvar xAxis = d3.svg.axis().scale(x).orient(\"bottom\");\r\nvar yAxis = d3.svg.axis().scale(y).orient(\"left\");\r\n\r\nvar svg = d3\r\n    .select(\"#chart\")\r\n    .attr(\"width\", iWidth)\r\n    .attr(\"height\", iHeight)\r\n    .append(\"g\")\r\n    .attr(\"transform\", \"translate(\" + margin.left + \",\" + margin.top + \")\");\r\n\r\nconsole.log(arrData);\r\n\r\nx.domain(d3.extent(arrData, function(d) { return d.date; }));\r\ny.domain(d3.extent(arrData, function(d) { return d.assets; }));\r\n\r\nsvg.append(\"g\")\r\n    .attr(\"class\", \"x axis\")\r\n    .attr(\"transform\", \"translate(0,\" + (iHeight - margin.top - margin.bottom) + \")\")\r\n    .call(xAxis);\r\n\r\nsvg.select(\".x.axis\")\r\n    .selectAll(\"text\")\r\n    .style(\"font-size\",\"9.25pt\");\r\n\r\nsvg.append(\"g\")\r\n    .attr(\"class\", \"y axis\")\r\n    .call(yAxis);\r\n\r\nsvg.select(\".y.axis\")\r\n    .selectAll(\"text\")\r\n    .style(\"font-size\",\"9.25pt\");\r\n\r\nsvg.append(\"path\")\r\n    .datum(arrData)\r\n    .attr(\"class\", \"revenues data\")\r\n    .attr(\"d\", d3.svg.line()\r\n        .x(function(d) { return x(d.date); })\r\n        .y(function(d) { return y(d.revenues); })\r\n    );\r\n\r\nsvg.append(\"path\")\r\n    .datum(arrData)\r\n    .attr(\"class\", \"expenses data\")\r\n    .attr(\"d\", d3.svg.line()\r\n        .x(function(d) { return x(d.date); })\r\n        .y(function(d) { return y(d.expenses); })\r\n    );\r\n    \r\n</script>\r\n<style>\r\n@media screen and (min-width: 0px) and (max-width: 640px) {\r\n    #chart {\r\n        width: 300px;\r\n        height: 150px;\r\n    }\r\n}\r\n@media screen and (min-width: 641px) and (max-width: 960px) {\r\n    #chart {\r\n        width: 600px;\r\n        height: 300px;\r\n    }\r\n}\r\n@media screen and (min-width: 961px) {\r\n    #chart {\r\n        width: 900px;\r\n        height: 450px;\r\n    }\r\n}\r\n</style>\r\n\r\n','Revenue vs Expenses','\0');
