INSERT INTO "report blocks" ( "GUID",  "Name",  "Parent Block GUID",  "Priority",  "Reports GUID",  "SQL Query" ) VALUES ('e20c0fe3559944f58fd3c2f4d831fa97', 'Row', NULL, 0, 'a92a85f5192e4dbb80cba3a67ad31448', 'SELECT * FROM \r\n    (\r\n    \tSELECT\r\n    \t\t"Transactions"."GUID",\r\n        \tDATE_FORMAT("Date", ''%Y-%m-%d'') AS "Date",\r\n    \t\t"Transactions"."Reference Number" AS "Number",\r\n    \t\t"Transaction Types"."Name" AS "Type",\r\n    \t\tCAST("Debits"."Debit" AS DECIMAL(64,2)) AS "Amount"\r\n    \tFROM\r\n    \t\t"Transactions"\r\n    \t\tJOIN "Transaction Types" ON "Transaction Types"."GUID" = "Transactions"."Transaction Types GUID"\r\n    \t\tJOIN ( \r\n    \t\t\tSELECT \r\n    \t\t\t\t"Transactions GUID", \r\n    \t\t\t\tSUM("Transaction Lines"."Debit") AS "Debit" \r\n    \t\t\tFROM \r\n    \t\t\t\t"Transaction Lines" \r\n    \t\t\tWHERE\r\n    \t\t\t\t"Transaction Lines"."Debit" > 0\r\n    \t\t\tGROUP BY "Transactions GUID"\r\n    \t\t) "Debits" ON "Transactions"."GUID" = "Transactions GUID"\r\n    \tWHERE\r\n    \t    DATE("Date") <= DATE({Ending})\r\n            AND DATE("Date") >= DATE({Starting})\r\n    ) TBL\t\r\nWHERE\r\n\t"Number" LIKE CONCAT(''%'', IFNULL({Number}, ''''), ''%'')\r\n\tAND "Type" LIKE CONCAT(''%'', IFNULL({Type}, ''''), ''%'')\r\n\tAND "Amount" LIKE CONCAT(''%'', IFNULL({Amount}, ''''), ''%'')\r\nORDER BY\r\n    "Date" DESC,\r\n    "Number" DESC,\r\n    "Amount" DESC\r\n    ' );
